import React from "react";
import "./CountDownClock.css";
const 
  minuteToSecond =  60,
  hourToMinute = 60,
  hourToSecond = minuteToSecond * 60,
  dayToSecond = hourToSecond * 24,
  dayToMili = dayToSecond * 1000;

class CountDownClock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      restTime:
        new Date("5/15/2021, 9:00:00 AM").getTime() - new Date().getTime(),
      days: 0,
      minutes: 0,
      seconds: 0,
    };

  }
  componentDidMount() {
    this.setTime = setInterval(() => this.countDown(), 1000);
  }
  componentWillUnmount() {
    clearInterval(this.setTime);
  }
  countDown() {
    if (this.state.restTime === 0) {
      this.turnOffTimer();
      return;
    }
    this.setState((preState) => {
      return { restTime: this.state.restTime - 1,
        days: parseInt(this.state.restTime / (1000 * 60 * 60 * 24)),
        hours: parseInt(this.state.restTime / (1000 * 60 * 60 * 24) % 60),
        minutes: parseInt((this.state.restTime / 1000 / 60) % 60),
        seconds: parseInt((this.state.restTime / 1000) % 60)}
    });
  }

  render() {
    return (
      <div className="clock-container">
        <div className="title">Countdown to HEAT</div>
        <div className="clock-by-unit">
          <ul>
            <li>
              <span>{this.state.days}</span>Day
            </li>
            <li>
              <span>
                {Math.floor((this.state.restTime /(1000*60*60*24) %60 ))}
              </span>
              Hour
            </li>
            <li>
              <span>
                {this.state.minutes} 
              </span>
              Minute
            </li> 
            <li>
              <span>
                {this.state.seconds}
              </span>
              Second
            </li>
          </ul>
        </div>
      </div>
    );
  }
}
export default CountDownClock;
