import React from 'react';
import './App.css';
class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        currentDate: new Date()
      }
    }
      turnOnUpdateTime()
      {
        this.timerID = setInterval(() => this.updateTime()),1000
      }

}


export default App;
